userDefinedLinkHintCss:
:root {
    --font-size: 14px;
    --font-size-hints: 12px;
    --font-size-url: 14px;

    --font-weight: normal;
    --font-weight-medium: medium;
    --font-weight-bold: bold;

    --font: "Helvetica Neue", "Helvetica", "Arial", sans-serif;

    --shadow: 0 2px 4px rgba(0, 0, 0, 0.12), 0 1px 2px rgba(0, 0, 0, 0.24);
    --shadow-vomnibar: 0 2px 6px rgba(0,0,0,0.3);

/* -------- VOMNIBAR & HUD -------- */
    --fg: #FFFFFF;
    --fg-dark: #aaaaaa;
    --fg-selected: #5294e2;

    --bg: #2f343f;
    --bg-searcharea: #2f343f;
    --bg-selected: #5294e2;

    --border: #2f343f;
    /* Do you like blue borders? */
    /* --border: #5294e2;  */

/* -------- HINTS -------- */
    --bg-hints: #2f343f;
    --fg-hints: #ffffff;
    --border-hints: #2f343f;
    /* Border color of link hints */
    /* --border-hints: #5294e2; */
}

/* --------------------------------- CSS --------------------------------- */

/* -------- HINTS -------- */
#vimium-hint-marker-container div.internalVimiumHintMarker, #vimium-hint-marker-container div.vimiumHintMarker {
    background: var(--bg-hints);
    border: 1px solid var(--border-hints);
    box-shadow: var(--shadow);
    padding: 2px 3px;
}

#vimium-hint-marker-container div span {
    color: var(--fg-hints);
    font-family: var(--font);
    font-size: var(--font-size-hints);
    font-weight: var(--font-weight-bold);
    text-shadow: none;
}

#vimium-hint-marker-container div > .matchingCharacter {
    opacity: 0.3;
}

#vimium-hint-marker-container div > .matchingCharacter ~ span {
    color: var(--fg-hints);
}

/* -------- VOMNIBAR -------- */
#vomnibar {
    box-shadow: var(--shadow-vomnibar);
    top: 18px;
    background: var(--bg-searcharea);
    border-radius: 2px;
    /* Blue Border */
    border: 1px solid var(--border)
}

#vomnibar .vomnibarSearchArea {
    background: var(--bg-searcharea);
    padding: 0;
    border-bottom: 0;
    border: 2px solid var(--bg-searcharea);
    box-shadow: var(--shadow);
}

#vomnibar input {
    background: var(--bg-searcharea);
    color: var(--fg);
    font-family: var(--font);
    border: 0;
    box-shadow: none;
    font-size: 24px;
    height: initial;
    padding: 12px 18px;
}

#vomnibar ul {
    background: var(--bg);
    margin: 0;
}

#vomnibar li {
    background: var(--bg);
    border: 0;
    display: flex;
    line-height: 1;
}

#vomnibar li .vomnibarTopHalf {
    font-size: var(--font-size);
    font-family: var(--font);
    font-weight: var(--font-weight-medium);
    margin: 0;
    padding: 0;
}

#vomnibar li .vomnibarBottomHalf {
    font-size: var(--font-size-url);
    font-family: var(--font);
    font-weight: var(--font-weight-medium);
    margin: 0;
    padding: 0;
}

#vomnibar li .vomnibarTopHalf {
    display: flex;
    flex: 0 0 67%;
    margin-right: 16px;
}

#vomnibar li .vomnibarBottomHalf {
    flex: 0 0 33%;
}

#vomnibar li .vomnibarSource.vomnibarNoInsertText,
#vomnibar li .vomnibarSource.vomnibarInsertText {
    display: none;
}

#vomnibar li .vomnibarUrl {
    color: var(--fg-dark);
}

#vomnibar li .vomnibarSource {
    color: var(--fg-dark);
}

#vomnibar li .vomnibarSource {
    display: block;
    flex-shrink: 0;
    font-size: 12px;
    font-weight: var(--font-weight-bold);
    line-height: 2;
    margin-right: 12px;
    text-transform: uppercase;
}

#vomnibar li .vomnibarTitle,
#vomnibar li .vomnibarUrl {
    line-height: 1.6;
}

#vomnibar li .vomnibarTitle {
    color: var(--fg);
    display: block;
    margin: 0;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

#vomnibar li .vomnibarUrl {
    padding-right: 6px;
    display: block;
    margin: 0;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

#vomnibar li .vomnibarMatch {
    color: inherit;
}

#vomnibar li .vomnibarTitle .vomnibarMatch {
    color: var(--fg-selected);
}

#vomnibar li.vomnibarSelected {
    background: var(--bg-selected);
}

#vomnibar li.vomnibarSelected .vomnibarUrl,
#vomnibar li.vomnibarSelected .vomnibarTitle,
#vomnibar li.vomnibarSelected .vomnibarSource {
    color: var(--fg);
}

#vomnibar li.vomnibarSelected .vomnibarMatch {
    color: inherit;
}

/* -------- HUD & Search bar -------- */
div.vimiumHUD {
    background: var(--bg);
    margin-bottom: 0.8rem;
    max-height: 1.9rem;
    font-size: 0.5rem;
    border-radius: 2px;
    /* Blue Border */
    border: 1px solid var(--border);
}

div.vimiumHUD span#hud-find-input,
div.vimiumHUD .vimiumHUDSearchAreaInner {
    color: var(--fg);
    font-family: var(--font);
    font-size: var(--font-size);
    font-weight: var(--font-weight-medium);
}

div.vimiumHUD span#hud-find-input:before {
    content: none;
}

div.vimiumHUD .hud-find {
    background-color: transparent;
    border: none;
}

div.vimiumHUD .vimiumHUDSearchArea {
    background-color: transparent;
    padding-top: 3px;
    padding-bottom: 3px;
    padding-left: 5px;
    padding-right: 3px;
}

body.vimiumFindMode ::selection {
    color: black;
}
